import React from "react";
import styled from "styled-components";
import Warm from '../assets/images/warm-bg.jpg';
import Cold from '../assets/images/cold-bg.jpg'

const WeatherStyles = styled.div`
  .container {
    width: 100%;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #7f5a83;
    background-image: linear-gradient(315deg, #7f5a83 0%, #0d324d 74%);

  }
  .search_box{
    width: 400px;
    height: 600px;
    border-radius: 20px;
    background-image: url(${Cold});
    background-size: cover;
    background-position: bottom;
    transition: .4s ease-out;
    padding: 25px;
  }
  .search_box.warm{
    background-image: url(${Warm})
  }
  .search_bar{
    width: 100%;
    display: block;
    padding: 20px;

    appearance: none;
    background: none;
    border: none;
    outline: none;

    background-color: rgba(255, 255, 255, 0.5);
    box-shadow: 0px 5px rgba(0, 0, 0, 0.2);
    border-radius: 15px;

    margin: 0 auto;

    color: #313131;
    font-size: 20px;

    transition: .4s ease;
  }
  .search_bar:focus{
    background-color: rgba(255, 255, 255, 0.75);
  }
  .location_box .location{
    color: #fff;
    font-size: 32px;
    font-weight: 500;
    text-align: center;
    text-shadow: 3px 3px rgba(50, 50, 70, 0.5);
    font-family: "RobotoMono Regular";

  }
`;

const APIKEY = {
  key: '31d20dee0c0bd6b6e437fbf39457c383',
  base: 'https://api.openweathermap.org/data/2.5/'
}

export default function Weather() {

  const dateBuilder = (d) => {
    let months = ['January', 'February', 'March',
                  'April', 'May', 'June',
                  'July', 'August', 'September',
                  'October', 'November', 'December']
    let days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday',
                'Friday', 'Saturday', 'Sunday']
    let day = days[d.getDay()]
    let date = d.getDate()
    let month = months[d.getMonth()]
    let year = d.getFullYear()

    return `${day} ${date} ${month} ${year}`
  }
  return (
    <WeatherStyles>
      <div className="container">
        <div className="search_box">
        <input 
        type="text" 
        className="search_bar"
        placeholder="Search..."/>
        <div className="location_box">
          <div className="location">Amsterdam, NL</div>
          <div className="date">{dateBuilder(new Date())}</div>
        </div>
        <div className="weather_box">
          <div className="temp">
            15C
          </div>
          <div className="weather">Sunny</div>
        </div>
        </div>
      </div>
    </WeatherStyles>
  );
}
