import React, { useState }from "react";
import styled from "styled-components";
import Warm from '../assets/images/warm-bg.jpg';
import Cold from '../assets/images/cold-bg.jpg'

const WeatherStyles = styled.div`
  .container {
    width: 100%;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #7f5a83;
    background-image: linear-gradient(315deg, #7f5a83 0%, #0d324d 74%);

  }
  .search_box{
    width: 400px;
    height: 600px;
    border-radius: 20px;
    background-image: url(${Cold});
    background-size: cover;
    background-position: bottom;
    transition: .3s ease-out;
    padding: 25px;
  }
  .search_box.warm{
    background-image: url(${Warm})
  }
  .search_bar{
    width: 100%;
    display: block;
    padding: 20px;

    appearance: none;
    background: none;
    border: none;
    outline: none;

    background-color: rgba(255, 255, 255, 0.5);
    box-shadow: 0px 5px rgba(0, 0, 0, 0.2);
    border-radius: 15px;

    margin: 0 auto;

    color: #313131;
    font-size: 20px;

    transition: .4s ease;
  }
  .search_bar:focus{
    background-color: rgba(255, 255, 255, 0.75);
  }
  .location{
    color: #fff;
    margin: 20px auto;
    font-size: 34px;
    font-weight: 500;
    text-align: center;
    text-shadow: 2px 2px rgba(50, 50, 70, 0.5);
    font-family: "Montserrat SemiBold";

  }
  .date{
    color:#fff;
    font-size: 24px;
    font-weight: 300;
    font-style: italic;
    text-align: center;
    text-shadow: 2px 2px rgba(50, 50, 70, 0.5);;
  }
  .weather_box{
    text-align: center;
  }
  .temp{
    position: relative;
    display: inline-block;
    margin: 30px auto;
    background-color: rgba(255, 255, 255, 0.2);
    padding: 16px;
    color: #fff;
    font-size: 102px;
    font-weight: 900;

    text-shadow: 2px 4px rgba(50, 50, 70, 0.5);
    text-align: center;
    box-shadow: 3px 6-x rgba(0, 0, 0, 0.2);
  }
  .weather{
    color: #fff;
    font-size: 48px;
    font-weight: 700;
    text-shadow: 3px 3px rgba(50, 50, 70, 0.5);
  }
`;

const api = {
  key: 'f8a3239850db33a6d8c80fa6a1e5236e',
  base: 'https://api.openweathermap.org/data/2.5/'
}

export default function Weather() {

  const [query, setQuery] = useState()
  const [weather, setWeather] = useState({})

  const search = evt => {
    if (evt.key === 'Enter') {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
          .then(res => res.json())
          .then(result => {
            setWeather(result)
            setQuery('')
            console.log(result)
          })
    }
  }
  const dateBuilder = (d) => {
    let months = ['January', 'February', 'March',
                  'April', 'May', 'June',
                  'July', 'August', 'September',
                  'October', 'November', 'December']
    let days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday',
                'Friday', 'Saturday', 'Sunday']
    let day = days[d.getDay()]
    let date = d.getDate()
    let month = months[d.getMonth()]
    let year = d.getFullYear()

    return `${day} ${date} ${month} ${year}`
  }
  return (
    <WeatherStyles>
      <div className="container">
        <div className={(typeof weather.main != "undefined") ? ((weather.main.temp > 16) ? 'search_box warm' : 'search_box') : 'search_box'}>
        <input 
        type="text" 
        className="search_bar"
        placeholder="Search..."
        onChange={e => setQuery(e.target.value)}
        value={query}
        onKeyPress={search}/>
        {(typeof weather.main != "undefined") ? (
        <div>
        <div className="location_box">
          <div className="location">{weather.name}, {weather.sys.country}</div>
          <div className="date">{dateBuilder(new Date())}</div>
        </div>
        <div className="weather_box">
          <div className="temp">
            {Math.round(weather.main.temp)}°c
          </div>
          <div className="temp_min">Min:
            {Math.round(weather.main.temp_min)}
          </div>
          <div className="temp_max">Max:
            {Math.round(weather.main.temp_max)}
          </div>
          <div className="weather">{weather.weather[0].main}</div>
        </div>
        </div>
         ) : ('')}
        </div>
      </div>
    </WeatherStyles>
  );
}
