import CurrentWeatherStyles from "./CurrentWeatherStyles";
import dateBuilder from "./DateBuilder";
import React, { useState, useEffect } from "react";

const api = {
  key: "f8a3239850db33a6d8c80fa6a1e5236e",
  base: "https://api.openweathermap.org/data/2.5/",
};

export default function CurrentWeather() {
  const [query, setQuery] = useState();
  const [weather, setWeather] = useState({});
  const [weatherDetails, setWeatherDetails] = useState({});
  const [weatherActive, setWeatherActive] = useState(false);
  //   const[isFlipped, setIsFlipped] = useState(false)

  // const handleClick = () => {
  //   setIsFlipped(!isFlipped)
  // }

  const handleSearch = (evt) => {
    if (evt.key !== "Enter") return;
    setQuery(evt.target.value);
  };
  // useEffect(() => {
  //   if(query) {
  //   fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
  //   .then((res) => res.json())
  //   .then((result) => {
  //     setWeather(result);
  //     // console.log(result);
  //     setWeatherActive(true)
  //   }).catch(error => console.log(error))
  // }
  // },[query])

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        );
        const data = await response.json();

        setWeather(data);
      } catch (error) {
        console.log(error);
      }
      fetchData();
    };
  }, [query]);

  useEffect(() => {
    if (weatherActive) {
      console.log("WEATHER", weather);
      const lat = weather.coord.lat;
      const lon = weather.coord.lon;
      console.log("lat", lat);
      console.log("lon", lon);
      fetch(
        `${
          api.base
        }onecall?lat=${lat}&lon=${lon}&exclude=${`minutely,hourly,alerts`}&appid=${
          api.key
        }`
      )
        .then((res) => res.json())
        .then((result) => {
          console.log(result);
          setWeatherDetails(result);
        })
        .catch((error) => console.log(error));
    }
  }, [weatherActive]);

  return (
    <CurrentWeatherStyles>
      <div
        className={
          typeof weather.main != "undefined"
            ? weather.main.temp > 16
              ? "search_box warm"
              : "search_box"
            : "search_box"
        }
      >
        <input
          type="text"
          className="search_bar"
          placeholder="Search..."
          onChange={(e) => setQuery(e.target.value)}
          value={query}
          onKeyPress={handleSearch}
        />
        {typeof weather.main != "undefined" ? (
          <div>
            <div className="location_box">
              <div className="location">
                {weather.name}, {weather.sys.country}
              </div>
              <div className="date">{dateBuilder(new Date())}</div>
            </div>
            <div className="weather_box">
              <div className="temp">{Math.round(weather.main.temp)}°c</div>
              <div className="temp_min">
                <span>Min temp: </span>
                {Math.round(weather.main.temp_min)}°c
              </div>
              <div className="temp_max">
                <span>Max temp: </span>
                {Math.round(weather.main.temp_max)}°c
              </div>
              <div className="humidity">
                <span>Humidity: </span>
                {weather.main.humidity}
              </div>
              <img
                src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
                alt=""
              />
              <div className="weather">{weather.weather[0].main}</div>
              {/* <button className="front_flip_btn" onClick={handleClick}>7 day forecast</button>  */}
            </div>
          </div>
        ) : (
          ""
        )}
      </div>
    </CurrentWeatherStyles>
  );
}
