import React, { useState, useEffect } from 'react'
import CurrentWeatherStyles from "./CurrentWeatherStyles";

const api = {
  key: "f8a3239850db33a6d8c80fa6a1e5236e",
  base: "https://api.openweathermap.org/data/2.5/",
};

export default function WeeklyWeather() {
  const [weatherDetails, setWeatherDetails] = useState({});
  const [weatherActive, setWeatherActive] = useState(false);
  const [weather, setWeather] = useState({});

  useEffect(() => {
    if (!weatherActive) return;

    console.log("WEATHER", weather);
    const lat = weather.coord.lat;
    const lon = weather.coord.lon;
    // console.log("lat", lat);
    // console.log("lon", lon);
    fetch(
      `${
        api.base
      }onecall?lat=${lat}&lon=${lon}&exclude=${`minutely,hourly,alerts`}&appid=${
        api.key
      }`
    )
      .then((res) => res.json())
      .then((result) => {
        console.log("RESULT", result);
        setWeatherDetails(result);
      })

      .catch((error) => console.log(error));
  }, [weather, weatherActive]);

  const [clicked, setClicked] = useState(null);
    const toggle = i => {
        if (clicked === i){
            return setClicked(null)
        }
        setClicked(i)
    }
  return (
    <CurrentWeatherStyles>
      <div className="weather_container">
      <div className="accordion-wrapper">
                {dailyArray.map((item, i) => (
                    <div className="accordion-item">
                        <div className="accordion-title" onClick={() => toggle(i) }>
                            {/* <p><DateConverter index={item} /></p> */}
                            {/* <p>{convertDate(item.dt)}</p> */}
                            {/* <p><IconConverter iconId={item.weather[0].id} /></p> */}
                            <p><span>{item.temp.min.toFixed(0)}/{item.temp.max.toFixed(0)}°C</span></p>
                            <p>{clicked === i ? "-" : "+" }</p>
                        </div>
                        <div className={clicked === i ? "accordion-content open" : "accordion-content"}>
                            <table className="accordion-table">
                            <tbody>
                                <tr>
                                    <th></th>
                                    <th>Ochtend</th>
                                    <th>Middag</th>
                                    <th>Avond</th>
                                    <th>Nacht</th>
                                </tr>
                                <tr>
                                    <td>Temp</td>
                                    <td>{item.temp.morn.toFixed(1)}°C</td>
                                    <td>{item.temp.day.toFixed(1)}°C</td>
                                    <td>{item.temp.eve.toFixed(1)}°C</td>
                                    <td>{item.temp.night.toFixed(1)}°C</td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                ))}
                </div>
      </div>
    </CurrentWeatherStyles>
  )
}
