import React from 'react'

export default function WeeklyWeather( {weather, onClick, itemName, itemContent, isActive}) {
  return (
    <div className={
      typeof weather.main != "undefined"
        ? weather.main.temp > 16
          ? "weather_container warm"
          : "weather_container"
        : "weather_container"
    }
    >
      <React.Fragment>
      <Header onClick={onClick}>
        {itemName}
        <HeaderIcon isActive={isActive} className='material-icons'>
          expand_more
        </HeaderIcon>
      </Header>
      <Content itemName={itemName} isActive={isActive}>
        <Inner id={itemName}>{itemContent}</Inner>
      </Content>
    </React.Fragment>
    </div>
  )
}



const AccordionContainer = styled.div`
overflow: hidden;
width: 18.75rem;
border-radius: 0.5rem;
background-color: #27262c;
color: #f9f9f9;
`

const Inner = styled.div`
position: absolute;
padding: 1rem;
color: #c3c1cb;
`
const Header = styled.button`
display: flex;
justify-content: space-between;
align-items: center;
width: 100%;
height: 4rem;
padding: 0 1rem;
font-size: 1rem;
text-align: left;
background: #212025;
color: inherit;
cursor: pointer;
border: none;
outline: none;
appearance: none;
`

const HeaderIcon = styled.span`
transform: rotate(${props => props.isActive ? -180 : 0}deg); 
transition: all .2s;
`

const Content = styled.div`
position: relative;
overflow: hidden;
height: ${props => {
  const inner = document.getElementById(props.itemName)
  return `${props.isActive && inner ? inner.clientHeight : 0}px`
}};
transition: height .35s;
`

export { AccordionContainer}
