import React from "react";
import styled from "styled-components";
import Warm from '../assets/images/warm-bg.jpg';
import Cold from '../assets/images/cold-bg.jpg'
const APIKEY = {
  key: '31d20dee0c0bd6b6e437fbf39457c383',
  base: 'https://api.openweathermap.org/data/2.5/'
}
const WeatherStyles = styled.div`
  .container {
    width: 100%;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #7f5a83;
    background-image: linear-gradient(315deg, #7f5a83 0%, #0d324d 74%);

  }
  .search_box{
    width: 400px;
    height: 600px;
    border-radius: 20px;
    background-image: url(${Cold});
    background-size: cover;
    background-position: bottom;
    transition: .4s ease-out;
    padding: 25px;
  }
  .search_box.warm{
    background-image: url(${Warm})
  }
  .search_bar{
    width: 100%;
    display: block;
    padding: 20px;

    appearance: none;
    background: none;
    border: none;
    outline: none;

    background-color: rgba(255, 255, 255, 0.5);
    box-shadow: 0px 5px rgba(0, 0, 0, 0.2);
    border-radius: 15px;

    margin: 0 auto;

    color: #313131;
    font-size: 20px;

    transition: .4s ease;
  }
`;

export default function Weather() {
  return (
    <WeatherStyles>
      <div className="container">
        <div className="search_box">
        <input 
        type="text" 
        className="search_bar"
        placeholder="Search..."/>
        </div>
      </div>
    </WeatherStyles>
  );
}
