    transition: background-color 0.6s ease;
    padding: 10px;
  }
  .accordion_title span {
    font-size: 20px;
  }
  .accordion_title img {
  }
  .accordion_title:hover {
  }
  .accordion_content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: rgba(255, 255, 255, 0.3);
