import React, { useState, useEffect } from 'react'
import CurrentWeatherStyles from "./CurrentWeatherStyles";


export default function WeeklyWeather({ weatherDetails }) {
  const [weeklyData, setWeeklyData] = useState([]);
  const [clicked, setClicked] = useState(null);
    const toggle = i => {
        if (clicked === i){
            return setClicked(null)
        }
        setClicked(i)
    }
  console.log(weatherDetails)

  const dailyArray = weeklyData.daily


  return (
    <CurrentWeatherStyles>
      {typeof weeklyData.daily != "undefined" ? (
        <div>
      <div className="weather_container">
      <div className="accordion-wrapper">
        {/* {weatherDetails &&
        <>

        </>
      } */}
      <p>{Math.round(weatherDetails.current.temp - 273)}</p>
        <p>{weatherDetails.daily[0].clouds}</p>
        <p>{weatherDetails.daily[1].clouds}</p>
        <p>{weatherDetails.daily[0].weather[0].main}</p>
        <p>{weatherDetails.daily[0].weather[0].description}</p>

                {dailyArray.map((item, i) => (
                    <div className="accordion-item">
                        <div className="accordion-title" onClick={() => toggle(i) }>
                            {/* <p><DateConverter index={item} /></p> */}
                            {/* <p>{convertDate(item.dt)}</p> */}
                            {/* <p><IconConverter iconId={item.weather[0].id} /></p> */}
                            <p><span>{weatherDetails.temp.min.toFixed(0)}/{weatherDetails.temp.max.toFixed(0)}°C</span></p>
                            <p>{clicked === i ? "-" : "+" }</p>
                        </div>
                        <div className={clicked === i ? "accordion-content open" : "accordion-content"}>
                            <table className="accordion-table">
                            <tbody>
                                <tr>
                                    <th></th>
                                    <th>Ochtend</th>
                                    <th>Middag</th>
                                    <th>Avond</th>
                                    <th>Nacht</th>
                                </tr>
                                <tr>
                                    <td>Temp</td>
                                    <td>{weatherDetails.temp.morn.toFixed(1)}°C</td>
                                    <td>{weatherDetails.temp.day.toFixed(1)}°C</td>
                                    <td>{weatherDetails.temp.eve.toFixed(1)}°C</td>
                                    <td>{weatherDetails.temp.night.toFixed(1)}°C</td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                ))}
                </div>
      </div>
      </div>
      ) : (
        <div></div>
    )}
    </CurrentWeatherStyles>
  )
}
